// Get the transcript array from the HTTP Request node
const transcripts = $input.item.json.transcript;

// Format function to group consecutive messages from same speaker
function formatTranscript(transcript) {
  let formattedTranscript = '';
  let currentSpeaker = null;
  let currentText = [];

  transcript.forEach((entry, idx) => {
    const speakerName = entry.speaker.display_name;
    const text = entry.text;

    if (speakerName === currentSpeaker) {
      currentText.push(text);
    } else {
      if (currentSpeaker !== null) {
        formattedTranscript += `**${currentSpeaker}:**\n${currentText.join(' ')}\n\n`;
      }
      currentSpeaker = speakerName;
      currentText = [text];
    }

    if (idx === transcript.length - 1) {
      formattedTranscript += `**${currentSpeaker}:**\n${currentText.join(' ')}`;
    }
  });

  return formattedTranscript.trim();
}

// Process both transcripts
const initialSalesCallTranscript = formatTranscript(transcripts[0]);
const latestClosingCallTranscript = formatTranscript(transcripts[1]);

// Return ONE item with both transcripts only
return {
  json: {
    initial_sales_call_transcript: initialSalesCallTranscript,
    latest_closing_call_transcript: latestClosingCallTranscript
  }
};

